import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

import "./style.css";

import api from "../../Services/api";

function Formulario() {
  const [formulario, setFormulario] = useState([]);

  const idFormulario = localStorage.getItem("idFormulario");
  const nomePlanilha = perguntas.map((perguntas) => perguntas.nome);
  const perguntaid = perguntas.map((perguntas) => perguntas.id);

  const history = useHistory();

  function respostas(e, index) {
    resposta[index] = e.target.value;
    setResposta([...resposta]);
  }

  async function submt(e) {
    e.preventDefault();

    try {
      for (var i = 0; i < perguntas.length; i++) {
        const data = {
          resposta: resposta[i],
        };
        await api.put("resposta", data, {
          headers: {
            id: perguntaid[i],
          },
        });
      }

      alert("Formulário preenchido com sucesso!");
      history.push("/");
    } catch (err) {
      alert("erro");
    }
  }

  useEffect(() => {
    api
      .get("listresposta", {
        headers: {
          Authorization: idFormulario,
        },
      })
      .then((response) => {
        setFormulario(response.data);
      });
  }, []);

  return (
    <div className="div-formularioRes">
      <h1 className="formulario-h1">{nomePlanilha[0]}</h1>
      <div>
        <ul className="formulario-ul">
          {perguntas.map((formulario) => (
            <li key={formulario.id}>
              <br />
              <label className="formulario-label">{formulario.pergunta}: </label>
              <p className="formulario-p">{formulario.resposta}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Formulario;
